import React from 'react'
import { Button, Modal } from 'react-bootstrap';
import SelectSearch from 'react-select-search';

export default class PerformSaleComponent extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            show: false,
            showFinalizeModal: false,
            itemType: '',
            brand: '',
            itemModel: '',
            processor: '',
            ramSize: '',
            hddSize: '',
            quantity: '',
            unitPrice: '',
            generation: '',
            cart: [],
            laptopBrandVisibility: true,
            currentItem: null,
            currentItemSupposedPrice: 0,
            searchCustomer: '',
            showSearchCustomerModal: false,
            customer: null
        }
        this.handleChange = this.handleChange.bind(this);


    }
    inventory = require('../../testData/inventory')
    customers = require('../../testData/customers')
    cus = ['larry', 'paul', 'luis']


    handleChange = async (evt) => {
        const value = evt.target.value;
        const name = evt.target.name;
        if (value === 'All Brands') {
            this.setState({ ...this.state, brand: '' })
        } else {
            await this.setState({
                ...this.state,
                [name]: value
            });
        }
    }

    saveChosenCustomer = (cus)=>{
        this.setState({...this.state, customer: cus})
    }
    displayCustomer = ()=>{
        if (this.state.customer != null) {
            return <p>Customer Name: {this.state.customer.name} | Phone #: {this.state.customer.number}</p>
        }
    }
    printCustomers = ()=> {
        return this.customers.map((customer,index)=>{
           if (customer.name.toLowerCase().includes(this.state.searchCustomer.toLowerCase())) {
               return <tr key={index}>
                   <td>{customer.name}</td>
                   <td>{customer.number}</td>
                   <td><button className='btn btn-primary' onClick={this.saveChosenCustomer(customer)}>Select</button> </td>
               </tr>
           }
        })
    }

    laptopBrand = () => {
        return <td>
            <div className='row'>
                <div className='col-12' >
                    <select className="form-control" name='brand' onChange={this.handleChange} >
                        <option selected disabled hidden>Brand</option>
                        <option>All Brands</option>
                        <option>Dell</option>
                        <option>HP</option>
                        <option>Lenovo</option>
                        <option>Apple</option>
                        <option>Acer</option>
                        <option>Asus</option>
                        <option>Toshiba</option>
                        <option>Phillips</option>
                        <option>Sony</option>
                        <option>Gateway</option>
                        <option>IBM</option>
                        <option>MSI</option>
                        <option>Samsung</option>
                        <option>Antect</option>
                        <option>Alienware</option>
                        <option>Transource</option>
                        <option>Epson</option>
                        <option>Canon</option>
                        <option>Xerox</option>
                        <option>Nec</option>
                        <option>LG</option>
                        <option>HCL</option>
                        <option>Others</option>
                        <option>Unknown</option>
                    </select>
                </div>
            </div>
        </td>
    }

    addItem = async (item) => {
        // console.log(item)
        let tempItem = { ...item }
        let cart = this.state.cart
        let tempCart = []
        let containsItem = false
        if (cart.length === 0) {
            tempItem.quantity = 1
            tempCart.push(tempItem)
        } else {
            await cart.map(itm => {
                if (itm.itemDescription === tempItem.itemDescription) {
                    itm.quantity = itm.quantity + 1
                    tempCart.push(itm)
                    containsItem = true
                } else {
                    tempCart.push(itm)
                }
            })
            if (containsItem == false) {
                tempItem.quantity = 1
                tempCart.push(tempItem)
            }
        }
        await this.setState({
            ...this.state,
            cart: tempCart
        });
        // console.log(this.state.cart)
    }

    handleDelete = (indx) => {
        let tempCart = this.state.cart
        let finalCart = tempCart.filter((item, index) => {
            return index !== indx
        })
        this.setState(
            {
                ...this.state,
                cart: finalCart,

            }
        )
    }

    pricingForItem = (itm) => {
        console.log('pricing for item called')
        if (itm.salesPrice == undefined || itm.salesPrice === 0) {
            return <button className='btn btn-primary' onClick={() => this.handleShow(itm)}>Add Price</button>
        }
        else {
            return itm.salesPrice + ' frs'
        }
    }

    showItemTotal = (itm) => {
        if (itm.salesPrice === undefined || itm.salesPrice === 0) {
            console.log('met undefined')
            return 0
        }
        return itm.quantity * itm.salesPrice
    }
    printCart = () => {
        // console.log(this.state.cart)
        return this.state.cart.map((itm, index) => {
            return <tr key={index}>
                <td>{index + 1}</td>
                <td>{itm.itemDescription}</td>
                <td>{itm.quantity}</td>
                <td>
                    {this.pricingForItem(itm)}
                </td>
                <td>
                    {this.showItemTotal(itm)} frs
                </td>
                <td>
                    <button className='btn btn-danger' onClick={() => this.handleDelete(index)}>X</button>
                </td>
            </tr>
        })
    }


    printInventory = (neededItemType, neededBrand) => {
        return this.inventory.map((item, index) => {
            if (item.itemType === neededItemType && this.state.brand === '') {
                //  this.setState({
                //     ...this.state,
                //     brand: ''
                // });
                return <tr key={index}>
                    <td key={index}>{index + 1}</td>
                    <td>{item.itemDescription}  </td>
                    <td>{item.quantity}</td>
                    <td>
                        <button type='button' onClick={() => this.addItem(item)} className='btn btn-primary'>+</button>
                    </td>
                </tr>
            } else if (item.itemType === neededItemType && item.brand === this.state.brand) {
                return <tr key={index}>
                    <td key={index}>{index + 1}</td>
                    <td>{item.itemDescription}  </td>
                    <td>{item.quantity}</td>
                    <td>
                        <input type="number" name="sellingPrice" id="" onChange={this.handleChange} />
                    </td>
                    <td>
                        <button type='button' onClick={() => this.addItem(item)} className='btn btn-primary'>+</button>
                    </td>
                </tr>
            }
        })
    }

    //***** For handling Modal to add price for an item to be sold ******
    handleClose = () => {
        this.setState({ ...this.state, show: false })
    };
    handleShow = (item) => {
        this.setState({ ...this.state, show: true, currentItem: item })
    };
    handleSavePrice = () => {
        let updateItem = this.state.currentItem
        updateItem.salesPrice = this.state.currentItemSupposedPrice
        //console.log(updateItem)
        this.setState({ ...this.state, show: false })
    }
    //***** End Modal functions for adding price to an item *****


    //***** Modal functions to finalize a customer's purchase ****
    handleCloseFinalizePurchase = () => {
        this.setState({ ...this.state, showFinalizeModal: false })
    };
    handleShowFinalizePurchase = () => {
        this.setState({ ...this.state, showFinalizeModal: true })
    };
    handleSavePurchase = () => {
        // let updateItem = this.state.currentItem
        // updateItem.salesPrice = this.state.currentItemSupposedPrice

        this.setState({ ...this.state, showFinalizeModal: false })
    }
    //**** End of Modal functions to finalize a customer's purchase *****



    //***** Modal functions to search a customer by name or phone number ****
    handleCloseSearchCustomer = () => {
        this.setState({ ...this.state, showSearchCustomerModal: false })
    };
    handleShowSearchCustomer = () => {
        this.setState({ ...this.state, showSearchCustomerModal: true })
    };
    handleChosenCustomer = () => {
        // let updateItem = this.state.currentItem
        // updateItem.salesPrice = this.state.currentItemSupposedPrice

        this.setState({ ...this.state, showSearchCustomerModal: false })
    }
    //**** End of Modal functions to search a customer by name or phone number *****


    printCartTotal = () => {
        let total = 0
        this.state.cart.map(itm => {
            console.log('total is: ', total)
            console.log('salesPrice: ', itm.salesPrice)
            console.log('qty is :', itm.quantity)
            if (itm.salesPrice === undefined) { }
            else {
                total = total + itm.salesPrice * itm.quantity
            }


        })
        return total.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")
    }

    render() {
        return <div className='container'>
            <div>{
                //Modal. This will be used as popup to assign price to an item
            }
                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header >
                        <Modal.Title>Adding item price</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <input className='form-control' type="Number" name='currentItemSupposedPrice' placeholder='Enter item price' onChange={this.handleChange} />
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleSavePrice}>
                            Save Price
                        </Button>
                    </Modal.Footer>
                </Modal>

                <Modal show={this.state.showFinalizeModal} onHide={this.handleCloseFinalizePurchase} backdrop="static" size='lg'>
                    <Modal.Header >
                        <Modal.Title>Payment Confirmation</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <label >Thank you for your purchase. Below is the summary of your purchase</label>
                        <br />
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleCloseFinalizePurchase}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleSavePurchase}>
                            Print Receipt
                        </Button>
                    </Modal.Footer>
                </Modal>

                <Modal show={this.state.showSearchCustomerModal} onHide={this.handleCloseSearchCustomer} backdrop="static" size='lg'>
                    <Modal.Header >
                        <Modal.Title>Chose Customer from the List of Customers</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <table>
                            {
                                this.printCustomers()
                            }
                        </table>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleCloseSearchCustomer}>
                            Close
                        </Button>
                    </Modal.Footer>
                </Modal>
                <div className='row'>
                    <div className='col-5 card'>
                        <div className='card-header bg-info'><b>Search Item Needed For Purchase</b></div>
                        <table className='table'>
                            <tbody>
                                <tr>
                                    <td>
                                        <div className='row'>
                                            <div className='col-12'>
                                                <select className="form-control" name='itemType' onChange={this.handleChange} >
                                                    <option selected disabled hidden>Select Item type</option>
                                                    <option> </option>
                                                    <option>Laptop</option>
                                                    <option>Desktop</option>
                                                    <option>Monitor</option>
                                                    <option>TV</option>
                                                    <option>Projector</option>
                                                    <option>Keyboard</option>
                                                    <option>Printer</option>
                                                    <option>Mouse</option>
                                                    <option>Power Cable</option>
                                                    <option>VGA/Display Cable</option>
                                                    <option>Other Accessories</option>
                                                    <option>Monitor Stand</option>
                                                </select>
                                            </div>
                                        </div>
                                    </td>
                                    {this.laptopBrand()}
                                </tr>
                            </tbody>
                        </table>

                        <br />
                        <div className='row '>
                            <div className='card-header bg-info'><b>Current Inventory</b></div>
                            <table className='table'>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Item Description</th>
                                        <th>Current Stock</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.printInventory(this.state.itemType,)}
                                </tbody>
                            </table>

                        </div>
                    </div>

                    <div className='col-7 '>
                        <div className='card-header bg-primary'>
                            <div class="input-group mb-3">
                                <input type="text" name='searchCustomer' onChange={this.handleChange} class="form-control" placeholder="Search Customer by Name or Phone Number" aria-label="Recipient's username" aria-describedby="basic-addon2" />
                                <div class="input-group-append">
                                    <button class="btn btn-outline-dark" type="button" onClick={this.handleShowSearchCustomer}>Search</button>
                                </div>

                            </div>
                        </div>
                        <div className='card-header bg-info'>
                            { //this.displayCustomer
                            }
                            <table>
                                <tr>
                                    <td style={{ float: 'right' }}>
                                        <button className='btn btn-primary' onClick={this.handleShowFinalizePurchase}>Finalize Purchase</button>
                                    </td>
                                </tr>
                            </table>
                            <b>SALES CART</b> <b style={{ float: 'right' }}>Total: {this.printCartTotal()}frs</b>
                        </div>

                        <table className='table'>
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Item Description</th>
                                    <th>Qty</th>
                                    <th>Unit Price</th>
                                    <th>Total</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.printCart()}
                            </tbody>
                        </table>
                    </div>

                </div>
            </div><br />

        </div>
    }
}
