import { data } from 'jquery';
import React from 'react'

export default class PerformSaleComponent extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            itemType: '',
            brand: '',
            itemModel: '',
            processor: '',
            ramSize: '',
            hddSize: '',
            quantity: '',
            unitPrice: '',
            generation: '',
            cart: [],
            laptopBrandVisibility: false,
        }
        this.handleChange = this.handleChange.bind(this);


    }
    data = require('../../testData/inventory')

    handleChange = async (evt) => {
        const value = evt.target.value;
        const name = evt.target.name;
        console.log(this.data)
        await this.setState({
            ...this.state,
            [name]: value
        });

        if (value === 'Laptop') {
            await this.setState({
                ...this.state,
                laptopBrandVisibility: !this.state.laptopBrandVisibility
            });
        }
    }

    laptopBrand = () => {
        return <div className='row'>
            <div className='col-4'>Brand:</div>
            <div className='col-8' >
                <select className="form-control" name='brand' onChange={this.handleChange} >
                    <option> </option>
                    <option>Dell</option>
                    <option>HP</option>
                    <option>Lenovo</option>
                    <option>Apple</option>
                    <option>Acer</option>
                    <option>Asus</option>
                    <option>Toshiba</option>
                    <option>Phillips</option>
                    <option>Sony</option>
                    <option>Gateway</option>
                    <option>IBM</option>
                    <option>MSI</option>
                    <option>Samsung</option>
                    <option>Antect</option>
                    <option>Alienware</option>
                    <option>Transource</option>
                    <option>Epson</option>
                    <option>Canon</option>
                    <option>Xerox</option>
                    <option>Nec</option>
                    <option>LG</option>
                    <option>HCL</option>
                    <option>Others</option>
                    <option>Unknown</option>
                </select>
            </div>
        </div>
    }

    printInventory = (fi)=>{
        let inventory = data
        console.log(inventory)
        // return inventory.map(item => {
        //     if (item.itemType === fi) {
        //         return <tr>
        //             <td>{item.itemType } </td>
        //         </tr>
        //     }
        // })
    }

    render() {
        return <div className='container'>
            <div>
                <div className='row'>
                    <div className='col-4 card'>
                        <div className='row'>
                            <div className='col-4'>Item_Type:</div>
                            <div className='col-8'>
                                <select className="form-control" name='itemType' onChange={this.handleChange} >
                                    <option> </option>
                                    <option>Laptop</option>
                                    <option>Desktop</option>
                                    <option>Monitor</option>
                                    <option>TV</option>
                                    <option>Projector</option>
                                    <option>Keyboard</option>
                                    <option>Printer</option>
                                    <option>Mouse</option>
                                    <option>Power Cable</option>
                                    <option>VGA/Display Cable</option>
                                    <option>Other Accessories</option>
                                    <option>Monitor Stand</option>
                                </select>
                            </div>
                        </div>
                        {this.state.laptopBrandVisibility && this.laptopBrand()}
                    </div>

                    <div className='col-8 card'></div>

                </div>
            </div>
            <div className='row card'>
                <table className='table'>
                    {this.printInventory('')}
                </table>

            </div> 
        </div>
    }
}
