import React, { PureComponent } from 'react'
import { withRouter, Link } from 'react-router-dom';

class LoginComponent extends PureComponent {

    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.login = this.login.bind(this);
    }
    handleClick() {
        console.log('Click happened');
    }

    login() {
        console.log('login button clicked')
        const { history } = this.props
        console.log(history)
        history.push('/AddToCart')
    }
    render() {
        const { history } = this.props;
        return <div className='card'>
            <div className='card-head bg-info'>
                <h3>Login to proceed</h3>
            </div>
            <div className='card-body'>

                <div className="input-group">
                    <div className="input-group-prepend">
                        <div class="input-group-text" id="btnGroupAddon">Username:</div>
                    </div>
                    <input type="text" class="form-control" placeholder="Enter username" name='username' aria-label="Input group example" aria-describedby="btnGroupAddon" />
                </div>
                <div className="input-group">
                    <div className="input-group-prepend">
                        <div class="input-group-text" id="btnGroupAddon">Password:</div>
                    </div>
                    <input type="password" class="form-control" placeholder="Enter password" name='password' aria-label="Input group example" aria-describedby="btnGroupAddon" />
                </div>
                <br />
                <button onClick={this.login} type="button" className="btn btn-primary btn-block">Login</button>
                <br />
            </div>
            <div className='card-footer'>
                <Link align='center'> Forgot password?</Link>
            </div>
        </div>
    }
}


export default withRouter(LoginComponent);
