import React from 'react'
import axios from 'axios'

export default class ViewSaleComponent extends React.Component {
    constructor() {
        super()
        this.state = {
            sales: [],
            searchField: ''
        }
    }
    handleChange = async (evt) => {
        const value = evt.target.value;
        const name = evt.target.name;

        await this.setState({
            ...this.state,
            [name]: value
        });
        console.log(this.state.searchField)
    }
    printSales = () => {
        let sales = this.state.sales
        if (this.state.searchField === '') {
            return sales.map((item, index) => {
                //console.log(item)
                return <tr key={index}>
                    <td>{item.date}</td>
                    <td>{item.confirmationNumber}</td>
                    <td>{item.customerName}</td>
                    <td>{item.customerNumber}</td>
                    <td>{item.itemsSoldSummary}</td>
                    <td>{item.total}</td>
                </tr>
            })
        } else {
            return sales.map((item, index) => {
                console.log('searchField:',this.state.searchField)
                console.log('customer:',item.customerName)
                if (!item.customerName.includes(this.state.searchField) /* || item.itemsSoldSummary.includes(this.state.searchField) */) {
                    return <tr key={index}>
                        <td>{item.date}</td>
                        <td>{item.confirmationNumber}</td>
                        <td>{item.customerName}</td>
                        <td>{item.customerNumber}</td>
                        <td>{item.itemsSoldSummary}</td>
                        <td>{item.total}</td>
                    </tr>
                }

            })
        }


    }

    componentDidMount() {
        axios.get("http://localhost:3012/getSales")
            .then(result => {
                var salesTemp = result.data.Sales
                this.setState({ ...this.state, sales: salesTemp })
                console.log('salesTemp is ', salesTemp)
            })
            .catch(err => {
                console.log('err occurred ', err)
            })
    }
    componentDidUpdate() {

    }
    render() {
        return <div>
            <div className='card-header bg-info'>
                <h4> SALES PERFORMED</h4>
                <div className="input-group mb-3">
                    <input type="text" name='searchField' onChange={this.handleChange} className="form-control" placeholder="Search Sale Record" aria-describedby="basic-addon2" />
                    <div className="input-group-append">
                        <button className="btn btn-outline-dark" type="button" onClick={this.handleShowSearchCustomer}>Search</button>
                    </div>
                </div>
            </div>
            <div>
                <table className='table table-striped table-dark table-hover'>
                    <thead className='thead-primary'>
                        <tr>
                            <th>Date</th>
                            <th>Confirmation #</th>
                            <th>Customer Name</th>
                            <th>Phone #</th>
                            <th>Items Sold</th>
                            <th>Total</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.printSales()}
                    </tbody>
                </table>
            </div>
            <p>View sale component works</p>
        </div>
    }
}
