import React from 'react'
import axios from 'axios'

export default class ViewCustomersComponent extends React.Component {
    constructor() {
        super()
        this.state = {
            customers: [],
            searchField: ''
        }
    }
    handleChange = async (evt) => {
        const value = evt.target.value;
        const name = evt.target.name;

        await this.setState({
            ...this.state,
            [name]: value
        });
    }
    printCustomers = () => {
        let customers = this.state.customers
        console.log('customers are: ', customers)
        if (this.state.searchField === '') {
            return customers.map((item, index) => {
                return <tr key={index}>
                    <td>{item.date}</td>
                    <td>{item.confirmationNumber}</td>
                    <td>{item.customerName}</td>
                    <td>{item.customerNumber}</td>
                    <td>{item.itemsSoldSummary}</td>
                    <td>{item.total}</td>
                </tr>
            })
        } else {
            return customers.map((item, index) => {
                if (item.customerName.toLowerCase().includes(this.state.searchField.toLowerCase())  || item.itemsSoldSummary.toLowerCase().includes(this.state.searchField.toLowerCase()) ) {
                    return <tr key={index}>
                        <td>{item.date}</td>
                        <td>{item.confirmationNumber}</td>
                        <td>{item.customerName}</td>
                        <td>{item.customerNumber}</td>
                        <td>{item.itemsSoldSummary}</td>
                        <td>{item.total}</td>
                    </tr>
                }

            })
        }


    }

    componentDidMount() {
        axios.get("http://localhost:3012/customers")
            .then(result => {
                var customersTemp = result.data.customers
                this.setState({ ...this.state, customers: customersTemp })
                console.log('customerTemp is ', customersTemp)
            })
            .catch(err => {
                console.log('err occurred ', err)
            })
    }
    componentDidUpdate() {

    }
    render() {
        return <div>
            <div className='card-header bg-info'>
                <h4>  Customers</h4>
                <div className="input-group mb-3">
                    <input type="text" name='searchField' onChange={this.handleChange} className="form-control" placeholder="Search Sale Record" aria-describedby="basic-addon2" />
                    <div className="input-group-append">
                        <button className="btn btn-outline-dark" type="button" onClick={this.handleShowSearchCustomer}>Search</button>
                    </div>
                </div>
            </div>
            <div>
                <table className='table table-striped table-dark table-hover'>
                    <thead className='thead-primary'>
                        <tr>
                            <th>Date</th>
                            <th>Confirmation #</th>
                            <th>Customer Name</th>
                            <th>Phone #</th>
                            <th>Items Sold</th>
                            <th>Total</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.printCustomers()}
                    </tbody>
                </table>
            </div>
        </div>
    }
}
