import React from 'react'
import { Button, ButtonToolbar, Modal } from 'react-bootstrap';


export default class PerformSaleComponent extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            show: false,
            itemType: '',
            brand: '',
            itemModel: '',
            processor: '',
            ramSize: '',
            hddSize: '',
            quantity: '',
            unitPrice: '',
            generation: '',
            cart: [],
            laptopBrandVisibility: true,
            currentItem: null,
            currentItemSupposedprice: 0
        }
        this.handleChange = this.handleChange.bind(this);


    }
    inventory = require('../../testData/inventory')

    handleChange = async (evt) => {
        const value = evt.target.value;
        const name = evt.target.name;
        console.log(name,value)
        if (value === 'All Brands') {
            this.setState({ ...this.state, brand: '' })
        } else {
            await this.setState({
                ...this.state,
                [name]: value
            });
        }
    }

    laptopBrand = () => {
        return <td>
            <div className='row'>
                <div className='col-12' >
                    <select className="form-control" name='brand' onChange={this.handleChange} >
                        <option selected disabled hidden>Brand</option>
                        <option>All Brands</option>
                        <option>Dell</option>
                        <option>HP</option>
                        <option>Lenovo</option>
                        <option>Apple</option>
                        <option>Acer</option>
                        <option>Asus</option>
                        <option>Toshiba</option>
                        <option>Phillips</option>
                        <option>Sony</option>
                        <option>Gateway</option>
                        <option>IBM</option>
                        <option>MSI</option>
                        <option>Samsung</option>
                        <option>Antect</option>
                        <option>Alienware</option>
                        <option>Transource</option>
                        <option>Epson</option>
                        <option>Canon</option>
                        <option>Xerox</option>
                        <option>Nec</option>
                        <option>LG</option>
                        <option>HCL</option>
                        <option>Others</option>
                        <option>Unknown</option>
                    </select>
                </div>
            </div>
        </td>
    }

    addItem = async (item) => {
        // console.log(item)
        let tempItem = { ...item }
        console.log('type of item is: ', typeof (tempItem))
        let cart = this.state.cart
        let tempCart = []
        let containsItem = false
        if (cart.length === 0) {
            tempItem.quantity = 1
            tempCart.push(tempItem)
        } else {
            await cart.map(itm => {
                if (itm.itemDescription === tempItem.itemDescription) {
                    itm.quantity = itm.quantity + 1
                    tempCart.push(itm)
                    containsItem = true
                } else {
                    tempCart.push(itm)
                }
            })
            if (containsItem == false) {
                tempItem.quantity = 1
                tempCart.push(tempItem)
            }
        }
        await this.setState({
            ...this.state,
            cart: tempCart
        });
        // console.log(this.state.cart)
    }

    handleDelete = (indx) => {
        let tempCart = this.state.cart
        let finalCart = tempCart.filter((item, index) => {
            return index !== indx
        })
        this.setState(
            {
                ...this.state,
                cart: finalCart,

            }
        )
    }


    printCart = () => {
        console.log(this.state.cart)
        return this.state.cart.map((itm, index) => {
            return <tr key={index}>
                <td>{index + 1}</td>
                <td>{itm.itemDescription}</td>
                <td>{itm.quantity}</td>
                <td><button className='btn btn-primary' onClick={() => this.handleShow(itm)}>Add Price</button>
                    {this.showModal}
                </td>
                <td></td>
                <td>
                    <button className='btn btn-danger' onClick={() => this.handleDelete(index)}>X</button>
                </td>
            </tr>
        })
    }


    printInventory = (neededItemType, neededBrand) => {
        return this.inventory.map((item, index) => {
            if (item.itemType === neededItemType && this.state.brand === '') {
                //  this.setState({
                //     ...this.state,
                //     brand: ''
                // });
                return <tr key={index}>
                    <td key={index}>{index + 1}</td>
                    <td>{item.itemDescription}  </td>
                    <td>{item.quantity}</td>
                    <td>
                        <input type="number" name="sellingPrice" id="" onChange={this.handleChange} />
                    </td>
                    <td>
                        <button type='button' onClick={() => this.addItem(item)} className='btn btn-primary'>+</button>
                    </td>
                </tr>
            } else if (item.itemType === neededItemType && item.brand === this.state.brand) {
                return <tr key={index}>
                    <td key={index}>{index + 1}</td>
                    <td>{item.itemDescription}  </td>
                    <td>{item.quantity}</td>
                    <td>
                        <input type="number" name="sellingPrice" id="" onChange={this.handleChange} />
                    </td>
                    <td>
                        <button type='button' onClick={() => this.addItem(item)} className='btn btn-primary'>+</button>
                    </td>
                </tr>
            }
        })
    }

    //for handling Modal to add price for an item to be sold
    handleClose = () => {
        this.setState({ ...this.state, show: false })
        // setShow(false)
    };
    handleShow = (item) => {
        this.setState({ ...this.state, show: true,currentItem: item })
       // this.setState({ ...this.state, currentItem: item })
    };
    handleSavePrice = ()=>{
        let updateItem = this.state.currentItem
        updateItem.salesPrice = this.state.currentItemSupposedprice
        console.log(updateItem)
    }


    render() {
        return <div className='container'>
            <div>{
            //Modal. This will be used as popup to assign price to an item
            }
                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header >
                        <Modal.Title>Adding item price</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <input className='form-control' type="Number" name='currentItemSupposedPrice' placeholder='Enter item price' onChange={this.handleChange} />
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleSavePrice}>
                            Save Price
                        </Button>
                    </Modal.Footer>
                </Modal>
                <div className='row'>
                    <div className='col-6 card'>
                        <div className='card-header bg-info'><b>Search Item Needed For Purchase</b></div>
                        <table className='table'>
                            <tbody>
                                <tr>
                                    <td>
                                        <div className='row'>
                                            <div className='col-12'>
                                                <select className="form-control" name='itemType' onChange={this.handleChange} >
                                                    <option selected disabled hidden>Select Item type</option>
                                                    <option> </option>
                                                    <option>Laptop</option>
                                                    <option>Desktop</option>
                                                    <option>Monitor</option>
                                                    <option>TV</option>
                                                    <option>Projector</option>
                                                    <option>Keyboard</option>
                                                    <option>Printer</option>
                                                    <option>Mouse</option>
                                                    <option>Power Cable</option>
                                                    <option>VGA/Display Cable</option>
                                                    <option>Other Accessories</option>
                                                    <option>Monitor Stand</option>
                                                </select>
                                            </div>
                                        </div>
                                    </td>
                                    {this.laptopBrand()}
                                </tr>
                            </tbody>
                        </table>

                        <br />
                        <div className='row '>
                            <div className='card-header bg-info'><b>Current Inventory</b></div>
                            <table className='table'>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Item Description</th>
                                        <th>Current Stock</th>
                                        <th>Selling Price</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.printInventory(this.state.itemType,)}
                                </tbody>
                            </table>

                        </div>
                    </div>

                    <div className='col-6 '>
                        <div className='card-header bg-info'><b>Cart</b></div>
                        <table className='table'>
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Item Description</th>
                                    <th>Quantity</th>
                                    <th>Unit Price</th>
                                    <th>Total</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.printCart()}
                            </tbody>
                        </table>
                    </div>

                </div>
            </div><br />

        </div>
    }
}
